/**
 * Banco de preguntas por categoría y por tema de guía.
 * Cada categoría tiene 5 temas; cada tema tiene su propio banco de 15 preguntas.
 * courseId = "categoria-carrera-N" con N 1..5 → tema índice N-1.
 * Las opciones se barajan al cargar para que la correcta no esté siempre en el mismo inciso.
 */

import type { QuizQuestion } from "@/services/quizService";
import { getCategorySlugFromCourseId, getTemaIndexFromCourseId } from "@/data/guiasEstudio";

function q(
  id: string,
  question: string,
  options: [string, string, string, string],
  correctIndex: number,
  explanation?: string
): QuizQuestion {
  return { id, question, options: [...options], correctAnswer: correctIndex, explanation };
}

/** Baraja las opciones de cada pregunta y actualiza correctAnswer para que la respuesta no esté siempre en el mismo inciso. */
function shuffleOptions(questions: QuizQuestion[]): QuizQuestion[] {
  const shuffle = <T,>(arr: T[]): T[] => {
    const out = [...arr];
    for (let i = out.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [out[i], out[j]] = [out[j], out[i]];
    }
    return out;
  };
  return questions.map((qn) => {
    const correctText = qn.options[qn.correctAnswer];
    const shuffled = shuffle(qn.options);
    const newIndex = shuffled.indexOf(correctText);
    return { ...qn, options: shuffled, correctAnswer: newIndex };
  });
}

